import React, { Component } from 'react';
import $ from 'jquery';
import banierrtrip from '../../static/images/banieretrip.png';
import logo from '../../static/images/BIRAMAWA LOGO 3D 8.png';
import './Trip.scss';
import lac from '../../static/images/686_ragdefault.png';
import image4 from '../../static/images/images.png';
import image3 from '../../static/images/image3.png';
import image2 from '../../static/images/images2.png';
import logotrips from '../../static/images/logotrips.png';


class Trip extends Component {
    constructor(){
        super();
        this.clearInterval();
        this.hauteurPage();
    }
    clearInterval(){
        let idI=parseInt(sessionStorage.getItem("idInterval"));
        console.log(idI);
        clearInterval(idI);
      }

    hauteurPage(){
      $(document).ready(()=>{
            let h1=window.outerHeight+30;
        // console.log(h);
        //  console.log(h1);
        //  document.getElementById("menu").style.height=h1+10+"px";
        //  document.getElementById("menu1").style.height=h1+"px";
          document.getElementById("container").style.minHeight="100vh";
          document.getElementById("container").style.maxHeight="100vh";
      });

    }
    render() {
        return (
        <div className="container" id="container">
          <div class="row" id="sect1">
            <div class="col-12" >
                <div class="row">
                    <div class="col-4">
                        <img width="100%" src={logotrips} alt="image yaris" style={{minWidth:"20%",maxWidth:"20%",marginLeft:"25%"}}/>
                    </div>
                    <div class="col-8" style={{paddingTop:'1%',paddingRight:'8%', textAlign:'right'}}>
                      <ul id="menuc" style={{marginRight:"5%"}}>
                        <li id="li1" onClick={()=>this.switchV(1)} style={{ color:"#FFFFFF"}}>BIRAMAWA TRIP</li>
                        <li id="li2" onClick={()=>this.switchV(2)} style={{color:"#FFFFFF"}}>SITES D'EXCEPTION</li>
                        <li id="li2" onClick={()=>this.switchV(3)} style={{color:"#FFFFFF"}}>HEBERGEMENT</li>
                        <li id="li2" onClick={()=>this.switchV(4)} style={{color:"#FFFFFF"}}>VOITURE</li>
                      </ul>
                    </div>
                </div>
                <div class="row">
                  <div class="col-2">
                    <i class="fas fa-align-left" style={{padding:'0.5rem',fontSize:'1.5em',marginLeft:"4%",marginTop:"25%",background: "#FFFFFF 0% 0% no-repeat padding-box",boxShadow: '0px 3px 6px #00000029',borderRadius: '3px 39px 39px 45px'}}></i>
                  </div>
                   <div class="col-10">

                  </div>
                </div>
                <div class="row titre" >
                  <div class="col-12">
                    <h1>
                        <span>Venez  Visiter  le  Sénégal</span>
                    </h1>
                  </div>
                </div>
                <div class="row">
                  <div class="col-4">
                  </div>
                  <div class="col-4" style={{backgroundColor:'white',color:'white',maxHeight:'0.3rem', fontSize:'0.1em'}}>
                        <span>Venez</span>
                  </div>
                </div>
                <div class="row" style={{marginTop:'2%',textAlign:'center', color:'white'}}>
                  <div class="col-12">
                    <h4>
                        Chercher un site
                    </h4>
                  </div>
                </div>
                <div class="row" >
                  <div class="col-4">
                  </div>
                  <div class="col-4" style={{paddingLeft:'2%',textAlign:'center'}}>
                    <form style={{width:"90%",border: '1px solid #FFFFFF',borderRadius: '17px'}} className="from-inline">
                      <input type="search" style={{width:"73%",borderLeft: '1px solid #FFFFFF',borderTop: '1px solid #FFFFFF',borderBottom: '1px solid #FFFFFF',borderRadius: '17px 0px 0px 17px',opacity: "0"}}/>
                      <input style={{maxWidth:'27%',minWidth:'27%',border: '1px solid #FFFFFF',borderRadius: '17px 17px 17px 17px',marginLeft:"0px",borderLeft: '0px solid #FFFFFF',opacity: "1",font: "Regular 18px/21px Arial",letterSpacing:"0px",background: '#FFFFFF 0% 0% no-repeat padding-box'}} type="button" value="OK" />
                    </form>
                  </div>
                </div>
                <div class="row" >
                  <div class="col-12" style={{marginTop:"4%",marginBottom:"2%"}}>
                      <div class="row">
                        <div class="col-2">
                          <div class="col-12">

                          </div>
                        </div>
                        <div class="col-7 pub" >
                          <div class="col-12 degrade">
                            <span class="titre">
                                Du nouveau chez
                            </span>
                            <span class="reduction">
                                -15%
                            </span>
                            <span class="voirplus">
                                En savoir plus <i class="fad fa-chevron-double-right" style={{color:"black"}}></i>
                            </span>
                          </div>
                        </div>
                      </div>
                  </div>
                </div>
                <div class="row " style={{position:"absolute",bottom:'0rem',left:'47%'}}>
                  <div class="col-12 align-middle">
                      <div style={{height:"0.2em",width:"3.7rem",backgroundColor:"rgba(255, 255, 255,0.6)",marginBottom:"0.2em"}}></div>
                      <div style={{height:"0.2em",width:"3.7em",backgroundColor:"rgba(255, 255, 255,0.8)",marginBottom:"0.2em"}}></div>
                      <div style={{height:"0.1em",width:"3.7rem",backgroundColor:"rgba(255, 255, 255,1)",marginBottom:"0.2em"}}></div>
                      <div id="triangle2" style={{borderTop: '20px solid rgb(255, 255, 255)'}}></div>
                  </div>
                </div>
              </div>
            </div>
        </div>
        );
    }
}

export default Trip;
