                            <img style={{maxWidth:'2.5rem'}} className="d-block w-100" src={instagram}  alt="Third slide"/>
                          </div>
                          <div className="col-2">
                          </div>
                        </div>
                      </div>
                    </div>

                  </div>
                </div>
              </div>
            </div>
        </div>
        );
    }
}

export default Trip;
