import React, { Component } from 'react';
import './Automobile.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import $ from 'jquery';
import imageyaris from '../../static/images/imageyaris.jpg';

class Automobile extends Component {
    constructor(){
      super();
      $("document").ready(()=>{
        this.switchV(1);
        }
      );
    }
    switchV(el){
      switch(el){
        case 1:{
          document.getElementById("vev").style.display="block";
          document.getElementById("li1").style.borderBottom="4px solid rgb(255, 208, 48)";

          document.getElementById("vel").style.display="none";
          document.getElementById("li2").style.borderBottom="0px";
          break;
        }
        case 2:{
          document.getElementById("vev").style.display="none";
          document.getElementById("li1").style.borderBottom="0px";

          document.getElementById("vel").style.display="block";
          document.getElementById("li2").style.borderBottom="4px solid rgb(255, 208, 48)";
          break;
        }
      }
    }
    hidePopup(){
      document.getElementById("popup").style.display="none";
    }
    showPopup(){
      document.getElementById("popup").style.display="block";
    }
    displayVev(){
      let tab=[{link:"",prix:"1 200 000"},{link:"",prix:"1 200 000"},{link:"",prix:"1 200 000"},{link:"",prix:"1 200 000"},{link:"",prix:"1 200 000"},{link:"",prix:"1 200 000"}];
      return tab.map((el,i)=>{
        return(
          <div onClick={()=>this.showPopup()} className="col-lg-3 col-md-3 col-xs-12 col-sm-12 imgauto">
              <div className="el" >
                <div>Marque :jfjfjfj</div>
                <div>Model :jfjfjfj</div>
                <div>Annee :jfjfjfj</div>
                <div>Kilometrage :jfjfjfj</div>
                <div>carburant :jfjfjfj</div>
                <div>Boite de vitesse :jfjfjfj</div>
                <div>prix de la voiture :jfjfjfj</div>
              </div>
              <div className="mag" style={{margin:"1.5em"}} >
                <img width="100%" src={imageyaris} alt="image yaris" />
              </div>
              <div>Toyota yaris 12000 0000</div>
          </div>
        )
      });
    }
    old(){
      return(
      <div>
      <div onClick={()=>this.showPopup()} className="col-lg-3 col-md- col-xs-12 col-sm-12 imgauto">
      <div className="el" >
        <div>Marque :jfjfjfj</div>
        <div>Model :jfjfjfj</div>
        <div>Annee :jfjfjfj</div>
        <div>Kilometrage :jfjfjfj</div>
        <div>carburant :jfjfjfj</div>
        <div>Boite de vitesse :jfjfjfj</div>
        <div>prix de la voiture :jfjfjfj</div>
      </div>
      <div className="mag" style={{margin:"1.5em"}} >
        <img width="100%" src={imageyaris} alt="image yaris" />
      </div>
      <div>Toyota yaris 12000 0000</div>
    </div>
    <div style={{boxShadow:"10px 5px 5px grey",margin:"1.5em"}} className="col-lg-3 col-md-3 col-xs-12 col-sm-12">
      <img width="100%" src={imageyaris} alt="image yaris" />
      <div>Toyota yaris 12000 0000</div>
    </div>
    <div style={{boxShadow:"10px 5px 5px grey",margin:"1.5em"}} className="col-lg-3 col-md-3 col-xs-12 col-sm-12">
      <img width="100%" src={imageyaris} alt="image yaris" />
      <div>Toyota yaris 12000 0000</div>
    </div>
    <div style={{boxShadow:"10px 5px 5px grey",margin:"1.5em"}} className="col-lg-3 col-md-3 col-xs-12 col-sm-12">
      <img width="100%" src={imageyaris} alt="image yaris" />
      <div>Toyota yaris 12000 0000</div>
    </div>
    <div style={{boxShadow:"10px 5px 5px grey",margin:"1.5em"}} className="col-lg-3 col-md-3 col-xs-12 col-sm-12">
      <img width="100%" src={imageyaris} alt="image yaris" />
      <div>Toyota yaris 12000 0000</div>
    </div>
    <div style={{boxShadow:"10px 5px 5px grey",margin:"1.5em"}} className="col-lg-3 col-md-3 col-xs-12 col-sm-12">
      <img width="100%" src={imageyaris} alt="image yaris" />
      <div>Toyota yaris 12000 0000</div>
    </div>
    </div>
      )
    }
    render() {
        return (
          <div id="content">
            <div>
              <div class="row">
                <div class="col-4">
                </div>
                <div class="col-8" >
                  <ul id="menuc" style={{marginRight:"2rem"}}>
                    <li id="li1" onClick={()=>this.switchV(1)}>VOITURES EN VENTE</li>
                    <li id="li2" onClick={()=>this.switchV(2)}>VOITURES EN LOCATION</li>
                  </ul>
                </div>
              </div>
              <div>
              </div>
              <div id="vev">
                <form style={{textAlign:"center"}} className="from-inline">
                  <input type="search" />
                  <input style={{backgroundColor:"rgb(255, 208, 48)",marginLeft:"0px"}} type="button" value="Recherche" />
                </form>
              </div>
              <div id="vel">
              <form style={{textAlign:"center"}} className="from-inline">
                  <input type="search" />
                  <input style={{backgroundColor:"rgb(255, 208, 48)",marginLeft:"0px"}} type="button" value="Recherche" />
                </form>
              <div style={{display:"none"}} id="popup">
                <span onClick={()=>this.hidePopup()} style={{position:"relative",left:"95%",top:"5px",width:"3%",border:"1px solid black",cursor:"pointer"}}>X</span>
                <div id="promoauto" style={{width:"90%",marginLeft:"auto",marginRight:"auto",height:"70px"}}>
                   <div id="backauto">
                     <div style={{marginLeft:"3em",marginTop:"0em",color:"white",display:"inline-block",fontSize:"1.5em"}}><b>Du nouveau chez</b></div>
                     <div id="p">-15</div>
                   </div>
                </div>
                  <div className="row">
                    <div style={{marginLeft:"1em",marginTop:"0.5em"}} className="col-lg-5 col-md-5 col-xs-5 col-sm-5 row">
                      <div id="img1" style={{marginBottom:"0.7em"}}  className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup">

                      </div>
                      <div style={{marginBottom:"0.7em"}} className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup img3">

                      </div>
                      <div style={{marginBottom:"0.7em"}} className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup img3">

                      </div>
                      <div style={{marginBottom:"0.7em"}} className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup img4">

                      </div>
                      <div style={{marginBottom:"0.7em"}} className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup img4">

                      </div>
                      <div style={{marginBottom:"0.7em"}} className="col-lg-4 col-md-4 col-xs-4 col-sm-4 imgautopopup img3">

                      </div>
                    </div>
                    <div id="imgpop" className="col-lg-6 col-md-6 col-xs-6 col-sm-6 row" style={{height:"200px"}}>
                          <div className="col-lg-1 col-md-1 col-xs-1 col-sm-1" style={{width:"20px",height:"30px",borderRadius:"50%",backgroundColor:"white",marginTop:"25%",textAlign:"center"}}><div style={{width:"0px",height:"0px",marginLeft:"-5px",marginRight:"auto",marginTop:"5px",borderTop:"10px solid transparent",borderBottom:"10px solid transparent",borderRight:"7px solid black"}}></div></div>
                          <div className="col-lg-1 col-md-1 col-xs-1 col-sm-1" style={{width:"20px",height:"30px",borderRadius:"50%",backgroundColor:"white",textAlign:"center",marginTop:"25%",marginLeft:"80%"}}><div style={{width:"0px",height:"0px",marginLeft:"auto",marginRight:"auto",marginTop:"5px",borderTop:"10px solid transparent",borderBottom:"10px solid transparent",borderLeft:"7px solid black"}}></div></div>
                    </div>
                    <div style={{width:"90%",marginLeft:"1.5em"}}>
                      <span style={{marginLeft:"2em",color:"white",backgroundColor:"#1492E6"}} className="btn primary">ACHETER</span>
                      <span style={{width:"60%",marginLeft:"15%"}}><b style={{color:"black",fontSize:"1.5em"}}>NOUVELLE YARIS</b><b style={{color:"rgb(255, 208, 48)",fontSize:"1.5em"}}> 13 000 000 FCFA</b></span>
                    </div>
                  </div>
                </div>
                <div  style={{width:"70%",marginLeft:"25%",marginTop:"2em"}} className="row">
                  {this.displayVev()}
                </div>
              </div>
            </div>
          </div>
        );
    }
}

export default Automobile;
