import React, { Component } from 'react';
import banierrtrip from '../../static/images/banieretrip.png';
import logo from '../../static/images/BIRAMAWA LOGO 3D 8.png';
import './Trip.scss';
import lac from '../../static/images/686_ragdefault.png';
import image4 from '../../static/images/images.png';
import image3 from '../../static/images/image3.png';
import image2 from '../../static/images/images2.png';


class Trip extends Component {
    constructor(){
        super();
        this.clearInterval();
    }
    clearInterval(){
        let idI=parseInt(sessionStorage.getItem("idInterval"));
        console.log(idI);
        clearInterval(idI);
      }

    hauteurPage(){
      $(document).ready(()=>{
            let h1=window.outerHeight+30;
        // console.log(h);
        //  console.log(h1);
        //  document.getElementById("menu").style.height=h1+10+"px";
        //  document.getElementById("menu1").style.height=h1+"px";
          document.getElementById("container").style.minHeight="100vh";
          document.getElementById("container").style.maxHeight="100vh";
      });

    }
    render() {
        return (
        <div className="container" id="container">
          <div class="row">
              <div class="col-4">
                  <img width="100%" src={logo} alt="image yaris" style={{minWidth:"15%",maxWidth:"15%",marginLeft:"25%"}}/>
              </div>
              <div class="col-8" >
                <ul id="menuc" style={{marginRight:"5%"}}>
                  <li id="li1" onClick={()=>this.switchV(1)} style={{ color:"#744A61"}}>BIRAMAWA TRIP</li>
                  <li id="li2" onClick={()=>this.switchV(2)} style={{color:"#744A61"}}>SITES D'EXCEPTION</li>
                  <li id="li2" onClick={()=>this.switchV(3)} style={{color:"#744A61"}}>HEBERGEMENT</li>
                  <li id="li2" onClick={()=>this.switchV(4)} style={{color:"#744A61"}}>VOITURE</li>
                </ul>
              </div>
          </div>
          <div class="row">
            <div class="col-2">
              <i class="fas fa-align-left" style={{fontSize:'1.5em',marginLeft:"4%",marginTop:"25%",background: "#FFFFFF 0% 0% no-repeat padding-box",boxShadow: '0px 3px 6px #00000029',borderRadius: '3px 39px 39px 45px'}}></i>
            </div>
             <div class="col-10">
                <form style={{marginLeft:"28%",marginTop:"2%",width:"auto"}} className="from-inline">
                  <input type="search" style={{border: "1px solid #E4E4E4"}}/>
                  <input style={{border: "1px solid #FFD332",backgroundColor:"#FFD332",marginLeft:"0px",opacity: "1",font: "Regular 18px/21px Arial",letterSpacing:"0px"}} type="button" value="Recherche" />
                </form>
            </div>
          </div>
            <div style={{width:"80%",marginLeft:"auto",marginRight:"auto"}} className="row">
                            <div style={{height:"150px"}} className="col-lg-3 col-md-3 col-xs-3 col-sm-3">
                                <img height="100%" width="100%" src={lac} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-3 col-xs-3 col-sm-3">
                                <img height="100%" width="100%" src={image4} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-3 3 col-sm-3">
                                <img height="100%" width="100%" src={lac} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-4 3 col-sm-3">
                                <img height="100%" width="100%" src={image3} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-4 3 col-sm-3">
                                <img height="100%" width="100%" src={image2} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-4 3 col-sm-3">
                                <img height="100%" width="100%" src={image3} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-4 3 col-sm-3">
                                <img height="100%" width="100%" src={image4} alt="" />
                            </div>
                            <div style={{height:"150px"}} className="col-lg-3 col-md-4 3 col-sm-3">
                                <img height="100%" width="100%" src={image2} alt="" />
                            </div>
                        </div>
        </div>
        );
    }
}

export default Trip;
