import React, { Component } from 'react';
import './Magazine.scss';
import $ from 'jquery';

import page1 from '../../static/images/page1.png';
import page2 from '../../static/images/page2.png';
import page3 from '../../static/images/page3.png';
import page4 from '../../static/images/page4.png';
import page5 from '../../static/images/page5.png';
import page6 from '../../static/images/page6.png';

import mag1 from '../../static/images/mag1.png';
import mag2 from '../../static/images/mag2.png';
import mag3 from '../../static/images/mag3.png';



class Magazine extends Component {
    state = {  }

    hauteurPage(){
      $(document).ready(()=>{
          document.getElementById("container").style.minHeight="100vh";
          document.getElementById("container").style.maxHeight="100vh";
      });

    }

    clearInterval(){
        let idI=parseInt(sessionStorage.getItem("idInterval"));
        console.log(idI);
        clearInterval(idI);
      }

    constructor(){
        super();
        this.clearInterval();
        // this.hauteurPage();
        // $("document").ready(()=>{
        //   this.switchV(1);
        //   }
        // );
    }
    render() {
        return (
          <div className="container" style={{minHeight:'100vh',maxHeight:'100vh'}}>
            <div className="row" style={{minHeight:'100%',maxHeight:'100%'}}>
              <div className="col-1" style={{minHeight:'100%',maxHeight:'100%'}}>
              </div>
              <div className="col-9" style={{minHeight:'100%',maxHeight:'100%'}}>
                <div className="row titrepage" >
                  <div className="col-12">
                    <h6><span style={{borderBottom:'4px solid #FFD030'}}>BIRAMAWA MAGAZINE</span></h6>
                  </div>
                </div>

                <div className="row" style={{minHeight:'50%',maxHeight:'50%',marginTop:'2rem'}}>
                  <div className="col-12 magazinebook" >
                    <div className="row" >
                      <div className="col-1" >
                        <i class="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'20rem'}}></i>
                      </div>
                      <div className="col-10 pageItem" >
                        <div className="row" >
                          <div className="col-1" >

                          </div>
                          <div className="col-10" >
                            <img  src={page1} alt="image yaris" />
                          </div>
                        </div>
                      </div>
                      <div className="col-1" >
                          <div className="row" >
                            <div className="col-12" >

                            </div>
                          </div>
                          <div className="row" >
                            <div className="col-12" style={{textAlign:'left'}}>
                              <i class="fas fa-angle-right"  style={{marginLeft:'55%',color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'20rem'}}></i>
                            </div>
                          </div>
                      </div>
                    </div>
                  </div>
                </div>

              </div>
              <div className="col-2" style={{minHeight:'50%',maxHeight:'50%',marginTop:"6rem"}}>
                <div className="row" style={{background: '#FFFFFF 0% 0% no-repeat padding-box',boxShadow: '1px 3px 6px #00000029',borderRadius: '62px',opacity: '1',marginLeft:'10%',minWidth:'80%',maxWidth:'80%',minHeight:'100%',maxHeight:'100%',backgroundColor:'white'}}>
                  <div className="col-12">
                    <div className="row" style={{marginTop:"1rem"}}>
                      <div className="col-12">
                        <i class="fas fa-angle-top" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                      </div>
                    </div>
                    <div className="row bodyListMagazine" style={{maxHeight: '100%'}}>
                      <div className="col-12" style={{maxHeight: '100%'}}>
                        <div className="row">
                          <div className="col-12" style={{maxHeight: '18vh',minHeight: '19vh',border:"1px solid black"}}>
                            df
                          </div>
                          <div className="col-12" style={{maxHeight: '18vh',minHeight: '19vh',border:"1px solid black"}}>
                            df
                          </div>
                          <div className="col-12" style={{maxHeight: '18vh',minHeight: '19vh',border:"1px solid black"}}>
                            df
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="row">
                      <div className="col-12">
                        <i class="fas fa-angle-bottom" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="row piedpage" >
              <div className="col-2" >

              </div>
              <div className="col-8" >
                <div className="row" >
                  <div className="col-1" >
                    <i class="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                  </div>
                  <div className="col-10" style={{boxShadow: '0px 3px 6px #00000029'}}>
                    <div className="row" >
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                      <div className="col-2" >
                        <img  src={page1} alt="image yaris" />
                      </div>
                    </div>
                  </div>
                  <div className="col-1" >
                    <i class="fas fa-angle-right" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                  </div>
                </div>

              </div>
              <div className="col-1" >

              </div>
            </div>
          </div>
        );
    }
}

export default Magazine;
