        return (
          <div className="container" style={{minHeight:'100vh',maxHeight:'100vh'}}>
            <div className="row" style={{minHeight:'100%',maxHeight:'100%'}}>
              <div className="col-1" style={{minHeight:'100%',maxHeight:'100%'}}>

              </div>
              <div className="col-10" style={{minHeight:'100%',maxHeight:'100%'}}>
                <div className="row titrepage" >
                  <div className="col-12">
                    <h6><span style={{borderBottom:'4px solid #FFD030'}}>BIRAMAWA MAGAZINE</span></h6>
                  </div>
                </div>

                <div className="row" style={{minHeight:'50%',maxHeight:'50%'}}>
                  <div className="col-12 magazinebook" >
                    <div className="row" >
                      <div className="col-1" >

                      </div>
                      <div className="col-10 pageItem" >
                          <img  style={{minHeight:'50%',maxHeight:'50%'}}src={page1} alt="image yaris" />
                      </div>
                      <div className="col-1" >

                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-1" style={{minHeight:'100%',maxHeight:'100%'}}>

              </div>
            </div>
          </div>
        );
    }
}

export default Magazine;
