import React, { Component } from 'react';
import './Admin.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {BrowserRouter as Router,Route,Switch,Link} from 'react-router-dom';
import $ from 'jquery';
import axios from 'axios';
import imageyaris from '../../static/images/imageyaris.jpg';


class Admin extends Component {


    constructor(props){
      super(props);
      this.state = {date: new Date(),nbrImgVoiture:[1],imagesCars:""}
    }


    nouvelleImagevoiture (){
       let tab = this.state.nbrImgVoiture;
       let length = tab.length;
       tab.push(length+1);
       this.setState({nbrImgVoiture:tab});
    }

    async uploadsImagesCar(myid){
      let input = document.getElementById("voitureInput"+myid);
      let img = document.getElementById("imgInput"+myid);
      let origine = "http://localhost/backend-birawam-slim/uploads/";
      let imagesCars = this.state.imagesCars;
      let src="";
      console.log(input)
      console.log(img)

      let url = "http://localhost/backend-birawam-slim/index.php/file/onUploadfile";

      const form = new FormData()
      form.set('uploads', input.files[0])

        axios({
          url:url,
          method:'post',
          headers: { 'Content-Type': 'multipart/form-data' },
          data:form
        }).then(rep=>{
          if(rep.status===200){
            let datas=rep.data;

            console.log(datas)
            src = origine+datas.generatedName;
            console.log(src);
            img.setAttribute("src",src);
            if(imagesCars==""){
              imagesCars = src;
            }else{
              imagesCars += ","+src;
            }
            this.setState({imagesCars:imagesCars});
            console.log(this.state.imagesCars);
          }else
            console.log(rep);
        });

    }
    render() {
        return (
          <div id="container">
            <div class="row">
              <div class="col-12">
                <div class="row" id='section1'>
                  <div class="col-12" style={{textAlign:'center'}}>
                    <h1>Ajouter Car</h1>
                  </div>
                  <div class="col-12" style={{textAlign:'center'}}>
                    <form style={{width:"50%",marginLeft:"25%"}}>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">Nom de la voiture</label>
                        <input type="text" class="form-control" id="nomVoityre"/>
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlSelect1">Description de la voiture</label>
                        <textarea class="form-control" id="descVoityre" rows="3"></textarea>
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlSelect2">Prix de la voiture</label>
                        <input type="number" class="form-control" id="prixVoityre" />
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">Numero de contacte</label>
                        <input type="text" class="form-control" id="telVoiture" />
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">Marque de la voiture</label>
                        <input type="text" class="form-control" id="marqueVoiture"/>
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">Model de la voiture</label>
                        <input type="text" class="form-control" id="modelVoiture" />
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">Annee de la voiture</label>
                        <div class="form-group">
                          <input type="number" class="form-control" id="anneeVoiture" />
                        </div>
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">kilometrage de la voiture</label>
                        <input type="text" class="form-control" id="kiloVoiture" />
                      </div>
                      <div class="form-group">
                        <label for="exampleFormControlInput1">carburant de la voiture</label>
                        <input type="text" class="form-control" id="carburant"/>
                      </div>

                      <div class="form-group">
                        <label for="exampleFormControlInput1">statut de la voiture</label>
                          <select  class="form-control" id="exampleFormControlSelect2">
                            <option value="vente">vente</option>
                            <option value="location">location</option>
                          </select>
                      </div>

                      <div class="form-group">
                        <label for="exampleFormControlInput1">les images de la voiture</label>
                          <div class="row" style={{border:'1px solid black'}}>
                            {this.state.nbrImgVoiture.map((image, index) => (
                              <div class="col-12" key={index}>
                                  <p>image {index + 1}</p>
                                  <div class="row">
                                    <div class="col-4" style={{paddingTop:"5%"}}>
                                      <input id={"voitureInput"+image} onChange={() => this.uploadsImagesCar(image)} className="imagesVoiture"  type="file" class="form-control"  placeholder="name@example.com"/>
                                    </div>
                                    <div class="col-8">
                                      <img id={"imgInput"+image} width="100%" src={imageyaris} alt="image yaris" style={{width:"50%"}} />
                                    </div>
                                   </div>
                              </div>
                            ))}
                              <div class="col-12" style={{marginBottom:'1rem'}}>
                                  <button  onClick={() => this.nouvelleImagevoiture()} type="button" class="btn btn-secondary">Nouvelle image</button>
                              </div>
                          </div>
                        </div>
                    </form>

                  </div>
                </div>
              </div>
            </div>
          </div>
        );
    }
}

export default Admin;
