      url: url,
      method: 'post',
      headers: {
        'Content-Type': 'multipart/form-data'
      },
      data: form
    }).then(rep => {
      if (rep.status === 200) {
        let datas = rep.data;
        datas.message.forEach((item, i) => {
          let el = {};
          let image = [];
          item.image = item.image.split(",");

          if (item.image.length == 1) {
            image.push(item.image[0], item.image[0], item.image[0], item.image[0], item.image[0]);
