import React, { useRef ,Component } from 'react';
import FlipPage from 'react-flip-page';
import './Magazine.scss';
import $ from 'jquery';

import page1 from '../../static/images/page1.png';
import page2 from '../../static/images/page7.png';
import page3 from '../../static/images/page3.png';
import page4 from '../../static/images/page6.png';
import page5 from '../../static/images/page5.png';
import page6 from '../../static/images/page6.png';

import mag1 from '../../static/images/mag1.png';
import mag2 from '../../static/images/mag2.png';
import mag3 from '../../static/images/mag3.png';
import magazine2 from '../../static/images/magazine2.jpeg';
import magazine3 from '../../static/images/magazine3.jpeg';
import logotrips from '../../static/images/logotrips.png';
import etendre from '../../static/images/etendre.png';



class Magazine extends Component {

    state = {  }

    flipPage = this;

    onNext = () => {
      this.flipPage.gotoNextPage();
    };

    onPrev = () => {
      this.flipPage.gotoPreviousPage();
    };

    magazineSave =  [
        {
          id:1,
          nom: "magazine 1",
          couverture: mag1,
          date:'01-05-2020',
          pages: [page1,page2,page3,page4,page5,page6]
        },
        {
          id:2,
          nom: "magazine 2",
          couverture: magazine2,
          date:'01-05-2020',
          pages: [page1,page2,page3,page4,page5,page6]
        },
        {
          id:3,
          couverture: magazine3,
          nom: "magazine 3",
          date:'01-05-2020',
          pages: [page1,page2,page3,page4,page5,page6]
        }
        ,
        {
          id:4,
          couverture: mag1,
          nom: "magazine 4",
          date:'01-05-2020',
          pages: [page1,page2,page3,page4,page5,page6]
        }
        ,
        {
          id:5,
          couverture: mag1,
          nom: "magazine 5",
          date:'01-05-2020',
          pages: [page1,page2,page3,page4,page5,page6]
        }
      ]
    magazines = [
      {
        id:1,
        nom: "magazine 1",
        couverture: mag1,
        date:'01-05-2020',
        pages: [page1,page2,page3,page4,page5,page6]
      },
      {
        id:2,
        nom: "magazine 2",
        couverture: magazine2,
        date:'01-05-2020',
        pages: [page1,page2,page3,page4,page5,page6]
      },
      {
        id:3,
        couverture: magazine3,
        nom: "magazine 3",
        date:'01-05-2020',
        pages: [page1,page2,page3,page4,page5,page6]
      }
    ]

    selectedmagazine = {
      id:3,
      couverture: mag1,
      nom: "magazine 3",
      date:'01-05-2020',
      pages: [page1,page2,page3,page4,page5,page6]
    };

    selectedPage = page1;

    pageIndex = 0;
    magazineIndex = 0;

    hauteurPage(){
      $(document).ready(()=>{
          document.getElementById("container").style.minHeight="100vh";
          document.getElementById("container").style.maxHeight="100vh";
      });

    }

    clearInterval(){
        let idI=parseInt(sessionStorage.getItem("idInterval"));
        console.log(idI);
        clearInterval(idI);
      }

    constructor(props){
        super(props);

        (this.magazines)[0] = this.magazineSave[0];
        (this.magazines)[1] = this.magazineSave[1];
        (this.magazines)[2] = this.magazineSave[2];

        this.state = {date: new Date(),magazines:this.magazines,selectedmagazine:this.magazines[0],selectedPage:this.selectedPage,indexPage:this.pageIndex,magazineIndex:2};

        this.clearInterval();
        // this.hauteurPage();


        $("document").ready(()=>{
            this.switchV(1);
            console.log(this.state.selectedmagazine);
            (document.getElementById('pageNextButton')).addEventListener('click',(e)=>{
               e.preventDefault();
               this.nextPage();
            });

            (document.getElementById('pagePreviousButton')).addEventListener('click',(e)=>{
               e.preventDefault();
               this.prevousPage();
            });

            (document.getElementById('pageNextButton0')).addEventListener('click',(e)=>{
               e.preventDefault();
               // this.nextPage();
               this.flipPage.gotoNextPage();

            });

            (document.getElementById('pagePreviousButton0')).addEventListener('click',(e)=>{
               e.preventDefault();
               // this.prevousPage();
               this.flipPage.gotoPreviousPage();

            });

            (document.getElementById('magazineNextButton')).addEventListener('click',(e)=>{
               e.preventDefault();
               this.nextMagazine();
            });

            (document.getElementById('magazinePreviousButton')).addEventListener('click',(e)=>{
               e.preventDefault();
               this.previousMagazine();
            });
          }
        );


    }

    selectMagazine(magazine,index){
      this.setState({selectedmagazine:magazine,selectedPage:magazine.pages[0],indexPage:index});
    }

    selectMagazineItem(page,index){
      this.setState({selectedPage:page,indexPage:index});
    }

    nextPage(){
      let index = this.state.indexPage;
      let page = "";
      if((index + 1) < (this.state.selectedmagazine).pages.length){
        index = index +1
        page = (this.state.selectedmagazine).pages[index]
        this.setState({selectedPage:page,indexPage:index});
      }else{
        page = (this.state.selectedmagazine).pages[0]
        this.setState({selectedPage:page,indexPage:0});
      }
    }

    prevousPage(){
      let index = this.state.indexPage;
      let page = "";
      if((index - 1) >=  0 ){
        index = index - 1;
        page = (this.state.selectedmagazine).pages[index]
        this.setState({selectedPage:page,indexPage:(index)});
      }else{
        index = (this.state.selectedmagazine).pages.length - 1;
        page = (this.state.selectedmagazine).pages[index]
        this.setState({selectedPage:page,indexPage:index});
      }
    }

    nextMagazine (){
      let index = this.state.magazineIndex;
      let magazine = {};

      let magazine1 = this.state.magazines[1];
      let magazine2 = this.state.magazines[2];

      if((index + 1) < (this.magazineSave).length){
        this.magazines[0] = (this.magazineSave)[index-1];
        this.magazines[1] = (this.magazineSave)[index];
        index = index +1
        this.magazines[2] =(this.magazineSave)[index];
        this.setState({magazines:this.magazines,magazineIndex:index});
      }else{
        index = 2;
        this.magazines[0] = this.magazineSave[0]
        this.magazines[1] = this.magazineSave[1]
        this.magazines[2] = this.magazineSave[2]
        this.setState({magazines:this.magazines,magazineIndex:index});
      }
    }


    previousMagazine (){
      let index = this.state.magazineIndex;
      let magazine = {};

      if((index - 1) >  0){
        index = index -1
        this.magazines[2] = (this.magazineSave)[index+1];
        this.magazines[1] = (this.magazineSave)[index];
        index = index -1;
        this.magazines[0] =(this.magazineSave)[index];
        this.setState({magazines:this.magazines,magazineIndex:index});
      }else{
        index = 2;
        this.magazines[0] = this.magazineSave[0]
        this.magazines[1] = this.magazineSave[1]
        this.magazines[2] = this.magazineSave[2]
        this.setState({magazines:this.magazines,magazineIndex:index});
      }
    }


    switchV(el){
      switch(el){
        case 1:{
          document.getElementById("zoom").style.display="none";

          break;
        }
        case 2:{
          document.getElementById("zoom").style.display="block";
          break;
        }
      }
    }


    render() {
        return (
          <div className="container" style={{minHeight:'100vh',maxHeight:'100vh'}} >
            <div id="nozoom">
              <div className="row" style={{minHeight:'100%',maxHeight:'100%'}}>
                <div className="col-1" style={{minHeight:'50%',maxHeight:'50%',marginTop:"0rem"}}>
                  <div className="row">
                    <div className="12">
                      <img  src={logotrips} alt="image yaris" />
                    </div>
                  </div>
                  <div className="row" style={{marginTop:"2.5rem"}}>
                    <div className="12">
                      <div className="row">
                        <div className="col-12">
                          <i className="fas fa-align-left" style={{padding:'0.5rem',fontSize:'1.5em',marginLeft:"3rem",marginTop:"25%",background: "#FFFFFF 0% 0% no-repeat padding-box",boxShadow: '0px 3px 6px #00000029',borderRadius: '3px 39px 39px 45px'}}></i>
                        </div>
                        <div className="col-12">
                          <span className="reduc" style={{position:"absolute",top:'10rem',left:'20%',padding:'1rem',backgroundColor:'#744A61',borderRadius:'50%',opacity:"1",color:'#FFD030',font: 'Bold 32px/48px Arial'}}>
                            -15
                          </span>
                          <span className="titre" style={{position:"absolute",top:"18rem",font: 'Bold 16px/24px Arial',letterSpacing: '0px',color: '#FFFFFF'}}>
                            Du nouveau <br /> chez <br />BIRAMAWA
                          </span>
                          <span className="plus" style={{position:"absolute",top:"25rem",right:'20%',padding:'0.5rem',backgroundColor:'#FFD030',color:'black'}}>
                            voir plus
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="row publeft">
                    <div className="col-12 ">

                    </div>
                  </div>

                </div>
                <div className="col-9" style={{minHeight:'100%',maxHeight:'100%'}}>
                  <div className="row titrepage" >
                    <div className="col-12">
                      <h6><span style={{borderBottom:'4px solid #FFD030'}}>BIRAMAWA MAGAZINE</span></h6>
                    </div>
                  </div>

                  <div className="row" style={{minHeight:'50%',maxHeight:'50%',marginTop:'2rem'}}>
                    <div className="col-12 magazinebook" id='magazinebook'>
                      <div className="row" >
                        <div className="col-1" >
                          <button onClick={() => this.flipPage.gotoNextPage()}>Next
                          </button>
                          <i id="pagePreviousButton0" className="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'20rem'}}></i>
                        </div>
                        <div className="col-10 pageItem" >
                          <div className="row" >
                            <div className="col-12" style={{padding:'0',margin:'0'}}>
                              <FlipPage orientation="orientation" width="100%"  height="700" ref={(component) => { this.flipPage = component; }}>
                                    <img src={page1} width="100%" height="100%"/>
                                    <img src={page1} width="100%" height="100%"/>
                              </FlipPage>
                            </div>
                          </div>
                        </div>
                        <div className="col-1" >
                            <div className="row" >
                              <div className="col-12">
                                <img onClick={()=>this.switchV(2)} src={etendre} alt="image yaris" style={{float:'right',marginTop:'1rem'}}/>
                              </div>
                            </div>
                            <div className="row" >
                              <div className="col-12" style={{textAlign:'left'}}>
                                <i id="pageNextButton0" className="fas fa-angle-right"  style={{marginLeft:'55%',color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'16rem'}}></i>
                              </div>
                            </div>
                        </div>
                      </div>
                    </div>
                  </div>

                </div>
                <div className="col-2" style={{minHeight:'50%',maxHeight:'50%',marginTop:"6rem"}}>
                  <div className="row" style={{background: '#FFFFFF 0% 0% no-repeat padding-box',boxShadow: '1px 3px 6px #00000029',borderRadius: '62px',opacity: '1',marginLeft:'10%',minWidth:'80%',maxWidth:'80%',minHeight:'100%',maxHeight:'100%',backgroundColor:'white'}}>
                    <div className="col-12">
                      <div className="row" style={{marginTop:"1rem"}}>
                        <div className="col-12">
                          <i id="magazinePreviousButton" className="fas fa-angle-up" style={{color: '#744A61',border:'1px solid #744A61', padding:'0.5rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                        </div>
                      </div>
                      <div className="row bodyListMagazine" style={{maxHeight: '100%'}}>
                        <div className="col-12" style={{maxHeight: '100%'}}>
                          <div className="row">
                            <div className="col-12" style={{maxHeight: '21vh',minHeight: '21vh',textAlign:"center",paddingTop:'2%'}}>
                              <img  src={mag1} alt="image yaris" style={{maxHeight: '80%',minHeight: '80%'}}/>
                              <p style={{color:'#744A61'}}>01-05-2020</p>
                            </div>
                              <div className="col-12" style={{maxHeight: '21vh',minHeight: '21vh',textAlign:"center",paddingTop:'2%'}}>
                                <img  src={mag1} alt="image yaris" style={{maxHeight: '80%',minHeight: '80%'}}/>
                                <p style={{color:'#744A61'}}>01-05-2020</p>
                              </div>
                              <div className="col-12" style={{maxHeight: '21vh',minHeight: '21vh',textAlign:"center",paddingTop:'2%'}}>
                                <img  src={mag1} alt="image yaris" style={{maxHeight: '80%',minHeight: '80%'}}/>
                                <p style={{color:'#744A61'}}>01-05-2020</p>
                              </div>
                          </div>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-12">
                          <i id="magazineNextButton" className="fas fa-angle-down" style={{color: '#744A61',border:'1px solid #744A61', padding:'0.5rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="row piedpage" >
                <div className="col-2" >

                </div>
                <div className="col-7" >
                  <div className="row" >
                    <div className="col-1" >
                      <i onClick={()=>this.prevousPage()} className="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                    </div>
                    <div className="col-10" style={{boxShadow: '0px 3px 6px #00000029'}}>
                      <div className="row" >
                        {this.state.selectedmagazine.pages.map((page, index) => (
                              <div   key={index} className="col-2" key={index}>
                                <img  onClick={()=>this.selectMagazineItem(page,index)} src={page} alt="image yaris" />
                              </div>
                        ))}
                      </div>
                    </div>
                    <div className="col-1" >
                      <i onClick={()=>this.nextPage()} className="fas fa-angle-right" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                    </div>
                  </div>

                </div>
                <div className="col-1" >

                </div>
              </div>
            </div>
            <div id="zoom">
              <div className="row" style={{minHeight:'100%',maxHeight:'100%'}}>
                <div className="col-1" style={{minHeight:'50%',maxHeight:'50%',marginTop:"0rem"}}>
                  <div className="row">
                    <div className="12">
                      <img  src={logotrips} alt="image yaris" />
                    </div>
                  </div>
                  <div className="row" style={{marginTop:"2.5rem"}}>
                    <div className="12">
                      <div className="row">
                        <div className="col-12">
                          <i className="fas fa-align-left" style={{padding:'0.5rem',fontSize:'1.5em',marginLeft:"3rem",marginTop:"25%",background: "#FFFFFF 0% 0% no-repeat padding-box",boxShadow: '0px 3px 6px #00000029',borderRadius: '3px 39px 39px 45px'}}></i>
                        </div>
                        <div className="col-12">
                          <span className="reduc" style={{position:"absolute",top:'10rem',left:'20%',padding:'1rem',backgroundColor:'#744A61',borderRadius:'50%',opacity:"1",color:'#FFD030',font: 'Bold 32px/48px Arial'}}>
                            -15
                          </span>
                          <span className="titre" style={{position:"absolute",top:"18rem",font: 'Bold 16px/24px Arial',letterSpacing: '0px',color: '#FFFFFF'}}>
                            Du nouveau <br /> chez <br />BIRAMAWA
                          </span>
                          <span className="plus" style={{position:"absolute",top:"25rem",right:'20%',padding:'0.5rem',backgroundColor:'#FFD030',color:'black'}}>
                            voir plus
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="row publeft">
                    <div className="col-12 ">

                    </div>
                  </div>

                </div>
                <div className="col-9" style={{minHeight:'100%',maxHeight:'100%'}}>
                  <div className="row titrepage" >
                    <div className="col-12">
                      <h6><span style={{borderBottom:'4px solid #FFD030'}}>BIRAMAWA MAGAZINE</span></h6>
                    </div>
                  </div>

                  <div className="row" style={{minHeight:'50%',maxHeight:'50%',marginTop:'2rem'}}>
                    <div className="col-1" >
                    </div>
                    <div className="col-11 magazinebook" id='magazinebook' style={{position:'fixed',top:"5%",left:'5%', zIndex:'999'}}>
                      <div className="row" >
                        <div className="col-1" >
                          <i id="pagePreviousButton" className="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'20rem'}}></i>
                        </div>
                        <div className="col-10 pageItem" >
                          <div className="row" >

                            <div className="col-12" >
                              <img  src={this.state.selectedPage} alt="image yaris" style={{minHeight:'91vh',maxHeight:'91vh'}}/>
                            </div>
                          </div>
                        </div>
                        <div className="col-1" >
                            <div className="row" >
                              <div className="col-12">
                                <img onClick={()=>this.switchV(1)} src={etendre} alt="image yaris" style={{float:'right',marginTop:'1rem'}}/>
                              </div>
                            </div>
                            <div className="row" >
                              <div className="col-12" style={{textAlign:'left'}}>
                                <i id="pageNextButton" className="fas fa-angle-right"  style={{marginLeft:'55%',color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'16rem'}}></i>
                              </div>
                            </div>
                        </div>
                      </div>
                    </div>
                  </div>

                </div>
                <div className="col-2" style={{minHeight:'50%',maxHeight:'50%',marginTop:"6rem"}}>
                  <div className="row" style={{background: '#FFFFFF 0% 0% no-repeat padding-box',boxShadow: '1px 3px 6px #00000029',borderRadius: '62px',opacity: '1',marginLeft:'10%',minWidth:'80%',maxWidth:'80%',minHeight:'100%',maxHeight:'100%',backgroundColor:'white'}}>
                    <div className="col-12">
                      <div className="row" style={{marginTop:"1rem"}}>
                        <div className="col-12">
                          <i className="fas fa-angle-up" style={{color: '#744A61',border:'1px solid #744A61', padding:'0.5rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                        </div>
                      </div>
                      <div className="row bodyListMagazine" style={{maxHeight: '100%'}}>
                        <div className="col-12" style={{maxHeight: '100%'}}>
                          <div className="row">
                            {this.state.magazines.map((magazine, index) => (
                                  <div   key={index} className="col-12" style={{maxHeight: '19vh',minHeight: '19vh',textAlign:"center",paddingTop:'2%'}}>
                                    <img onClick={()=>this.selectMagazine(magazine,index)} src={magazine.couverture} alt="image yaris" style={{maxHeight: '80%',minHeight: '80%'}}/>
                                    <p style={{color:'#744A61'}}>magazine.date</p>
                                  </div>
                            ))}
                          </div>
                        </div>
                      </div>
                      <div className="row">
                        <div className="col-12">
                          <i className="fas fa-angle-down" style={{color: '#744A61',border:'1px solid #744A61', padding:'0.5rem',borderRadius:"50%", marginLeft:'40%'}}></i>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="row piedpage" >
                <div className="col-2" >

                </div>
                <div className="col-7" >
                  <div className="row" >
                    <div className="col-1" >
                      <i className="fas fa-angle-left" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                    </div>
                    <div className="col-10" style={{boxShadow: '0px 3px 6px #00000029'}}>
                      <div className="row" >
                        {this.state.selectedmagazine.pages.map((page, index) => (
                              <div  key={index}  className="col-2"  key={index}>
                                <img  onClick={()=>this.selectMagazine(page,index)} src={page} alt="image yaris" />
                              </div>
                        ))}
                      </div>
                    </div>
                    <div className="col-1" >
                      <i className="fas fa-angle-right" style={{color: '#744A61',border:'1px solid #744A61', padding:'1rem',borderRadius:"50%", marginTop:'35%'}}></i>
                    </div>
                  </div>

                </div>
                <div className="col-1" >

                </div>
              </div>
            </div>
          </div>
        );
    }
}

export default Magazine;
